import React from 'react';
import logo from './logo.svg';
import './App.css';
import ReactMarkdown from 'react-markdown'

class App extends React.Component {
  constructor(props) {
      super(props);
      this.state = {postContent: "", postMetadata: "", error: null}
  }

  componentDidMount() {
      fetch("http://localhost:9000/blog")
          .then(response => response.json())
          .then((result) => {this.setState({isLoaded: true, postContent: result.content, postMetadata: result.metadata})})
  }

  render() {
    return (
      <div className="App">
        <Header/>
        <Post postContent = {this.state.postContent} metaData = {this.state.postMetadata}/>
      </div>
    ) 
  }
}

class Header extends React.Component {
   render() {
      return (
      <header className="header">
        <NavBar/>
      </header>
      );
   }
}

class Post extends React.Component {
   render() {
      return (
      <div className="post">
        <ReactMarkdown source = {this.props.postContent} />
        <PostMetadata data = {this.props.metaData} />
      </div>
      );
   }
}

class PostMetadata extends React.Component {
   render() {
      return (
        <div className="metadata">
          <h1> {this.props.data.title} </h1>
          {this.props.data.description}
        </div>
      );
   }
}

class NavBar extends React.Component {
  render() {
  return (
    <nav className="navbar">
      <ul>
          <li> <a href="#about">About</a> </li>
          <li> <a href="#portfolio">Portfolio</a> </li>
          <li> <a href="#writing">Writing</a> </li>
          <li> <a href="#music">Music</a> </li>
          <li> <a href="#contact">Contact</a> </li>
      </ul> 
    </nav>);
  }
}

export default App;
