import React from 'react';
import blackArrowRight from './blackRightArrow.png'
import blackArrowLeft from './blackLeftArrow.png'
import greyArrowRight from './greyRightArrow.png'
import greyArrowLeft from './greyLeftArrow.png'
import './App.css';
import ReactMarkdown from 'react-markdown'

class App extends React.Component {
  constructor(props) {
      super(props);
      this.state = {postContent: "", postMetadata: "", error: null, currentPost: 1}
  }

  componentDidMount() {
      fetch("http://localhost:9000/blog/1")
          .then(response => response.json())
          .then((result) => {this.setState({isLoaded: true, postContent: result.content, postMetadata: result.metadata})})
  }

  handleArrowClick(){

  }

  render() {
    return (
      <div className="App">
        <ChangePostArrow highlightedImage = {blackArrowRight} unhighlightedImage = {greyArrowRight} arrowType = "nextArrow" currentPost = {this.state.currentPost}/>
        <ChangePostArrow highlightedImage = {blackArrowLeft} unhighlightedImage = {greyArrowLeft} arrowType = "previousArrow" currentPost = {this.state.currentPost}/>
        <Header/>
        <Post postContent = {this.state.postContent} metaData = {this.state.postMetadata}/>
      </div>
    )
  }
}

class ChangePostArrow extends React.Component {

  constructor(props) {
      super(props);
      this.handleMouseHover = this.handleMouseHover.bind(this);
      this.state = {highlightedImage: props.highlightedImage, unhighlightedImage:props.unhighlightedImage, hasAnotherPost: true, currentPost: props.currentPost, isHovering: false, arrowType: props.arrowType}
  }

  handleMouseHover() {
    this.setState({isHovering: !this.state.isHovering});
  }

  handleClick() {

  }

  chooseImage()
  {
    return this.state.isHovering && this.state.hasAnotherPost ? this.state.highlightedImage : this.state.unhighlightedImage
  }

  render() {
    return (
      <img className={this.state.arrowType}
           src={this.chooseImage()}
           onMouseEnter={this.handleMouseHover}
           onMouseLeave={this.handleMouseHover}
           onclick = {this.handleClick()} />
    )
  }  
}

class Header extends React.Component {
   render() {
      return (
      <header className="header">
        <NavBar/>
      </header>
      );
   }
}

class Post extends React.Component {
   render() {
      return (
      <div className="post">
        <PostMetadata data = {this.props.metaData} />
        <ReactMarkdown source = {this.props.postContent} />
      </div>
      );
   }
}

class PostMetadata extends React.Component {
   render() {
      return (
        <div className="metadata">
          <h1 className = "title"> {this.props.data.title} </h1>
          <p className = "author"> by <i> {this.props.data.author} </i> </p>
          <p className = "published-date"> <i> {this.props.data.published} </i> </p>
        </div>
      );
   }
}

class NavBar extends React.Component {
  render() {
  return (
    <nav className="navbar">
      <ul>
          <li> <a href="http://localhost:9000/archive">Archive</a> </li>
          <li> <a href="http://localhost:9000/blog/1">Posts</a> </li>
      </ul> 
    </nav>);
  }
}

export default App;
