import React from 'react';
import logo from './logo.svg';
import blackArrowRight from './blackRightArrow.png'
import blackArrowLeft from './blackLeftArrow.png'
import greyArrowRight from './greyRightArrow.png'
import greyArrowLeft from './greyLeftArrow.png'
import './App.css';
import ReactMarkdown from 'react-markdown'

class App extends React.Component {
  constructor(props) {
      super(props);
      this.state = {postContent: "", postMetadata: "", error: null}
  }

  componentDidMount() {
      fetch("http://localhost:9000/blog/1")
          .then(response => response.json())
          .then((result) => {this.setState({isLoaded: true, postContent: result.content, postMetadata: result.metadata})})
  }

  render() {
    return (
      <div className="App">
        <NextPostArrow highlightedImage= {blackArrowRight} />
        <Header/>
        <Post postContent = {this.state.postContent} metaData = {this.state.postMetadata}/>
      </div>
    )
  }
}

class NextPostArrow extends React.Component {

  constructor(props) {
      super(props);
      this.handleMouseHover = this.handleMouseHover.bind(this);
      this.state = {highlightedImage: null, unhighlightedImage:null, hasAnotherPost: true, currentPost: 0, isHovering: false}
  }

  handleMouseHover() {
    this.setState(this.toggleHoverState);
  }

  toggleHoverState(state) {
    return {
      isHovering: !state.isHovering,
    }
  }

  render() {
    return (
      <img className="nextPostArrow" src={this.state.isHovering && hasAnotherPost ? highlightedImage : unhighlightedImage} onMouseEnter={this.handleMouseHover} onMouseLeave={this.handleMouseHover}/>
    )
  }  
}

class Header extends React.Component {
   render() {
      return (
      <header className="header">
        <NavBar/>
      </header>
      );
   }
}

class Post extends React.Component {
   render() {
      return (
      <div className="post">
        <PostMetadata data = {this.props.metaData} />
        <ReactMarkdown source = {this.props.postContent} />
      </div>
      );
   }
}

class PostMetadata extends React.Component {
   render() {
      return (
        <div className="metadata">
          <h1 className = "title"> {this.props.data.title} </h1>
          <p className = "author"> by <i> {this.props.data.author} </i> </p>
          <p className = "published-date"> <i> {this.props.data.published} </i> </p>
        </div>
      );
   }
}

class NavBar extends React.Component {
  render() {
  return (
    <nav className="navbar">
      <ul>
          <li> <a href="http://localhost:9000/archive">Archive</a> </li>
          <li> <a href="http://localhost:9000/blog/1">Posts</a> </li>
      </ul> 
    </nav>);
  }
}

export default App;
