import React from 'react';
import logo from './logo.svg';
import './App.css';
import ReactMarkdown from 'react-markdown'

class App extends React.Component {
  constructor(props) {
      super(props);
      this.state = { aggregatedPosts: "" };
  }

  callAPI() {
      fetch("http://localhost:9000/blog")
          .then(response => response.text())
          .then(response => this.setState({ aggregatedPosts: response }));
  }

  componentWillMount() {
      this.callAPI();
  }

  render() {
    return (
      <div className="App">
        <Header />
        <AggregatedPosts aggregatedPosts = {this.state.aggregatedPosts} />
      </div>
    ) 
  }
}

class Header extends React.Component {
   render() {
      return (
      <header className="header">
        <NavBar/>
      </header>
      );
   }
}

class AggregatedPosts extends React.component {
   render() {
      return (
      <div className="aggregatedPosts">
        <ReactMarkdown source={this.props.aggregatedPosts} />
      </div>
      );
   }
}

class NavBar extends React.Component {
  render() {
  return (
    <nav className="navbar">
      <ul>
          <li> <a href="#about">About</a> </li>
          <li> <a href="#portfolio">Portfolio</a> </li>
          <li> <a href="#writing">Writing</a> </li>
          <li> <a href="#music">Music</a> </li>
          <li> <a href="#contact">Contact</a> </li>
      </ul> 
    </nav>);
  }
}

class Post extends React.Component {

}

export default App;
